recommendations.cssfix:
/* Prevent horizontal scrolling */
* {
  box-sizing: border-box;
}

body {
  overflow-x: hidden;
  max-width: 100vw;
}

/* Ensure images don't overflow */
img {
  max-width: 100%;
  height: auto;
}

@media screen and (max-width: 768px) {
  body, p, span, div {
    font-size: 16px !important;
    line-height: 1.5 !important;
  }

  small, .small-text {
    font-size: 14px !important;
  }
}

@media screen and (max-width: 768px) {
  button, 
  a, 
  input[type="submit"], 
  input[type="button"] {
    min-height: 44px;
    min-width: 44px;
    padding: 12px 16px;
  }
}

@media screen and (max-width: 768px) {
  .main-navigation {
    display: none;
  }

  .hamburger-menu {
    display: block;
  }

  .mobile-nav-active .main-navigation {
    display: block;
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    background: white;
    z-index: 1000;
  }
}
